// –Ω–∞ –≤—Å—è–∫–∏–π —Å–ª—É—á–∞–π —É–±–µ—Ä—ë–º –æ–¥–∏–Ω–æ—á–Ω—ã–µ –æ—Å—Ç–∞–≤—à–∏–µ—Å—è –∑–≤—ë–∑–¥–æ—á–∫–∏
text = text.replace(/\*/g, '');

// 2) –§—É–Ω–∫—Ü–∏—è —ç–∫—Ä–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏—è HTML
function escapeHtml(str) {
  return str
    .replace(/&/g, "&amp;")
    .replace(/</g, "&lt;")
    .replace(/>/g, "&gt;")
    .replace(/"/g, "&quot;")
    .replace(/'/g, "&#39;");
}

// –°–Ω–∞—á–∞–ª–∞ —ç–∫—Ä–∞–Ω–∏—Ä—É–µ–º –≤–æ–æ–±—â–µ –≤—Å—ë
let escaped = escapeHtml(text);

// 3) –ü–æ—Ç–æ–º –≤–æ–∑–≤—Ä–∞—â–∞–µ–º —Ä–∞–∑—Ä–µ—à—ë–Ω–Ω—ã–µ —Ç–µ–≥–∏ –∏–∑ –æ—Ä–∏–≥–∏–Ω–∞–ª—å–Ω–æ–≥–æ —Ç–µ–∫—Å—Ç–∞
allowedTags.forEach(tag => {
  const tagPattern = new RegExp(`<${tag}(\\s[^>]*?)?>[\\s\\S]*?<\\/${tag}>`, "gi");
  const selfClosePattern = new RegExp(`<${tag}(\\s[^>]*?)?\\/>`, "gi");

  let match;
  while ((match = tagPattern.exec(text)) !== null) {
    const originalFragment = match[0];              // <b>...</b>
    const escapedFragment = escapeHtml(originalFragment); // &lt;b&gt;...&lt;/b&gt;
    escaped = escaped.split(escapedFragment).join(originalFragment);
  }

  while ((match = selfClosePattern.exec(text)) !== null) {
    const originalFragment = match[0];
    const escapedFragment = escapeHtml(originalFragment);
    escaped = escaped.split(escapedFragment).join(originalFragment);
  }
